import { useLocaleConfig } from '@vuepress/helper/client';
import { nextTick, onMounted, onUnmounted, watch } from 'vue';
import { usePageData } from 'vuepress/client';
import { usePhotoSwipeOptions } from '../helpers/index.js';
import { getImages, registerPhotoSwipe } from '../utils/index.js';
import 'photoswipe/dist/photoswipe.css';
import '../styles/photo-swipe.css';
export const usePhotoSwipe = ({ selector, locales, delay = 500, scrollToClose = true, }) => {
    const photoSwipeOptions = usePhotoSwipeOptions();
    const locale = useLocaleConfig(locales);
    const page = usePageData();
    let destroy = null;
    const setupPhotoSwipe = () => new Promise((resolve) => setTimeout(resolve, delay))
        .then(() => nextTick())
        .then(async () => {
        destroy = await registerPhotoSwipe(getImages(selector), {
            ...photoSwipeOptions,
            ...locale.value,
        }, scrollToClose);
    });
    onMounted(() => {
        setupPhotoSwipe();
        watch(() => page.value.path, () => {
            destroy?.();
            setupPhotoSwipe();
        });
    });
    onUnmounted(() => {
        destroy?.();
    });
};
